					location: Utils.getAbsoluteUrl(getExtensionAssetsPath('img/icons-set-2/icn-location.png')),
					audience: Utils.getAbsoluteUrl(getExtensionAssetsPath('img/icons-set-2/icn-role-solidblue.png')),
