					CDInPerson: Utils.getAbsoluteUrl(getExtensionAssetsPath('img/icons-set-2/icn-format-in-person.png')),
					CDVirtual: Utils.getAbsoluteUrl(getExtensionAssetsPath('img/icons-set-2/icn-format-live-online.png')),
